import { Component } from '@angular/core';
import {Router} from "@angular/router";

@Component({
  selector: 'app-main-page',
  templateUrl: './main-page.component.html',
  styleUrls: ['./main-page.component.css']
})
export class MainPageComponent {

  constructor(
    readonly router: Router
  ) { }

}
<h2>Esta es la Main Page</h2>

<h3>Contenido de la Main Page</h3>

<nav>
  <ul>
    <li><a routerLink="./articulos-page" routerLinkActive="active">Ver Articulos</a></li>
    <li><a routerLink="./fotos-page" routerLinkActive="active">Ver Fotos</a></li>
  </ul>
</nav>
